import { useState } from 'react';
import './App.css';
import Header from "./componentes/Header/Header"
import Banner from './componentes/Banner/Banner';
import Formulario from './componentes/Formulario/Formulario';
import TituloCategoria from './componentes/TituloCategoria/TituloCategoria';
import Categoria from './componentes/Categoria/Categoria';
import Footer from './componentes/Footer/Footer';

function App() {

  const [mostrarFormulario, actualizarMostrar] = useState(true)
  const [videos, actualizarVideos] = useState([])

  //Ternario --> condicion ? seMuestra : noSeMuestra
  // condicion && seMuestra

  const cambiarMostrar = () => {
    actualizarMostrar(!mostrarFormulario)
  }
    //Registrar video

  const registrarVideo = (video) => {
    console.log("Nuevo video", video)
    //Spread operator
    actualizarVideos([...videos, video])
  }


  //Lista de categorias
  const categorias = [
    {
      titulo: "Prog 1",
      titulocategoria: "Programación",
      imagen:"",
      video:"",
      descripcion:"",
      colorPrimario: "#57C278"
    },
    {
      titulo: "¿Cuándo usar let, var, y const?",
      titulocategoria: "Front End",
      imagen:"./img/front1.png",
      video:"",
      descripcion:"",
      colorPrimario: "#82CFFA"
    },
    {
      titulo: "Data 1",
      titulocategoria: "Data Science",
      imagen:"",
      video:"",
      descripcion:"",
      colorPrimario: "#A6D157"
    },
    {
      titulo: "Dev 1",
      titulocategoria: "Devops",
      imagen:"",
      video:"",
      descripcion:"",
      colorPrimario: "#E06B69"
    },
    {
      titulo: "UI 1",
      titulocategoria: "UX y Diseño",
      imagen:"",
      video:"",
      descripcion:"",
      colorPrimario: "#DB6EBF"
    },
    {
      titulo: "M 1",
      titulocategoria: "Móvil",
      imagen:"",
      video:"",
      descripcion:"",
      colorPrimario: "#FFBA05"
    },
    {
      titulo: "Inn 1",
      titulocategoria: "Innovación y Gestión",
      imagen:"",
      video:"",
      descripcion:"",
      colorPrimario: "#FF8A29"
    }
  ]

  return (
    <div className="App">
      <Header/>
      <Banner/>
      {
        mostrarFormulario && <Formulario
          categorias={categorias.map((categoria) => categoria.titulocategoria)}
          registrarVideo={registrarVideo}
        />
      }
      <TituloCategoria cambiarMostrar={cambiarMostrar}/>
      {
        categorias.map((categoria) => <Categoria
          datos={categoria}
          key={categoria.titulo}
          videos={videos.filter(video => video.titulocategoria === categoria.titulocategoria)          }
        />)
      }
      <Footer/>
    </div>
  );
}

export default App;
