import { useState } from "react"
import "./Formulario.css"
import CampoTexto from "../CampoTexto/CampoTexto";
import ListaCategorias from "../ListaCategorias/ListaCategorias";
import Boton from "../Boton/Boton";


const Formulario = (props) => {


    const [titulo, actualizarTitulo] = useState("")
    const [categoria, actualizarCategoria] = useState("")
    const [imagen, actualizarImagen] = useState("")
    const [video, actualizarVideo] = useState("")
    const [descripcion, actualizarDescripcion] = useState("")

    const {registrarVideo} =props

    const manejarEnvio = (e) => {
        e.preventDefault()
        console.log("Manejar el envio")
        let datosAEnviar = {
            titulo,
            categoria,
            imagen,
            video,
            descripcion
        }
        registrarVideo(datosAEnviar)
    }

    return (
    <section className="contenedorFormulario">
        <form onSubmit={manejarEnvio}>
            <h2>EDITAR CARD:</h2>
            <CampoTexto
                titulo="Título"
                placeholder="Ingresar título"
                required
                valor={titulo}
                actualizarValor={actualizarTitulo}
            />
            <ListaCategorias
                valor={categoria}
                actualizarEquipo={actualizarCategoria}
                categorias={props.categorias}
            />
            <CampoTexto
                titulo="Imagen"
                placeholder="Ingresar imagen"
                required
                valor={imagen}
                actualizarValor={actualizarImagen}
            />
            <CampoTexto
                titulo="Video"
                placeholder="Ingresar video"
                required
                valor={video}
                actualizarValor={actualizarVideo}
            />
            <CampoTexto
                titulo="Descripción"
                placeholder="Ingresar descripción"
                required
                valor={descripcion}
                actualizarValor={actualizarDescripcion}
            />

            <section className="botones">
                <Boton>EDITAR</Boton>
                <Boton>ELIMINAR</Boton>
            </section>
        </form>
    </section>
)
}

export default Formulario;
